mapper.insert:

  SELECT LAST_INSERT_ID()

insert into bus_site (name, alias, englishName, 
  area, section, status, 
  description, position, createDate
  )
values (#{name,jdbcType=VARCHAR}, #{alias,jdbcType=VARCHAR}, #{englishName,jdbcType=VARCHAR}, 
  #{area,jdbcType=VARCHAR}, #{section,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{position,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}
  )

mapper.select:
select
from bus_site where 1=1
and( name like CONCAT(CONCAT('%', #{keywords}),'%') or alias like CONCAT(CONCAT('%', #{keywords}),'%') or englishName like CONCAT(CONCAT('%', #{keywords}),'%') or area like CONCAT(CONCAT('%', #{keywords}),'%') or section like CONCAT(CONCAT('%', #{keywords}),'%') or description like CONCAT(CONCAT('%', #{keywords}),'%') or position like CONCAT(CONCAT('%', #{keywords}),'%') )
order by ${sort}
${order}
