mapper.update:
update kw_comment_count
set commentId = #{commentId,jdbcType=INTEGER},
  agreeCount = #{agreeCount,jdbcType=INTEGER},
  opposeCount = #{opposeCount,jdbcType=INTEGER},
  creater = #{creater,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select count(agreeCount) from kw_comment_count
where commentId = #{commentId,jdbcType=INTEGER} and  creater = #{creater,jdbcType=VARCHAR}
and agreeCount = 1

select count(opposeCount) from kw_comment_count
where commentId = #{commentId,jdbcType=INTEGER} and  creater = #{creater,jdbcType=VARCHAR}
and opposeCount = 1
