mapper.select:
select

from sys_notice
where 1=1

  and(
  title like CONCAT(CONCAT('%',#{keywrods}),'%')
  or
  type like CONCAT(CONCAT('%',#{keywords}),'%')
  )


  order by ${sort}


  ${order}

mapper.update:
update sys_notice


    status = '已失效'


where deadline is not null and deadline < #{deadline}
