mapper.select:
select from t_recording_track where 1=1
and( calling_no like CONCAT(CONCAT('%', #{keywords}),'%') or called_no like CONCAT(CONCAT('%', #{keywords}),'%') or ext_no like CONCAT(CONCAT('%', #{keywords}),'%') or agent_no like CONCAT(CONCAT('%', #{keywords}),'%') )
order by ${sort}
${order}

select

from t_recording_track


      and (
	      calling_no like  CONCAT(CONCAT('%', #{keywords}),'%')
	      or
	      called_no like  CONCAT(CONCAT('%', #{keywords}),'%')
	      or
	      ext_no like  CONCAT(CONCAT('%', #{keywords}),'%')
	      or
	      agent_no like  CONCAT(CONCAT('%', #{keywords}),'%')
      )


    	and date_format(start_time, '%Y-%m-%d') >= #{reStartDate}


    	and date_format(end_time, '%Y-%m-%d') <= #{reEndDate}


    	and time_format(start_time, '%h') >= #{reStartHour}


    	and time_format(end_time, '%H') <= #{reEndHour}




    			and unix_timestamp(end_time)-unix_timestamp(start_time) >= #{period}


    			and unix_timestamp(end_time)-unix_timestamp(start_time) <= #{period}




    	direction = convert(#{direction}, signed)
